<?php		if(!empty($_POST))	{		$msg=array();				if(empty($_POST['nm']) || empty($_POST['email']) || empty($_POST['query']))		{			$msg[]="Please full fill all requirement";		}								if(is_numeric($_POST['fnm']))		{			$msg[]="Name must be in String Format...";		}				if(is_numeric($_POST['email']))			{			$msg[]="Name must be in appropriate Format...";		}						if(!empty($msg))		{			echo '<b>Error:-</b><br>';						foreach($msg as $k)			{				echo '<li>'.$k;			}		}		else		{			$nm=$_POST['nm'];			$email=$_POST['email'];			$question=$_POST['query'];						$link=mysql_connect("localhost","root","vertrigo")or die("Can't Connect...");						mysql_select_db("ebook-store",$link) or die("Can't Connect to Database...");						$query="insert into contactus(cid,cname,cemail,cquery)			values(NULL,'$nm','$email','$question')";						mysql_query($query,$link) or die("Can't Execute Query...");						header("location:contact.php");		}	}	else	{		header("location:index.php");	}?>