<?phpif(!empty($_POST))	{		$msg = "";					if(empty($_POST['fnm']) || empty($_POST['unm']) || empty($_POST['gender']) || empty($_POST['pwd']) || empty($_POST['cpwd']) || empty($_POST['mail']))		{			$msg.="<li>Please fill all fields";		}				if($_POST['pwd']!=$_POST['cpwd'])		{			$msg.="<li>Please enter your password again";		}				if(!ereg("^[a-z0-9_]+[a-z0-9_.]*@[a-z0-9_-]+[a-z0-9_.-]*\.[a-z]{2,5}$",$_POST['mail']))		{			$msg.="<li>Please enter a valid email address";		}						if(strlen($_POST['pwd'])>10)		{			$msg.="<li>Please enter email address and password in correct format";		}				if(is_numeric($_POST['fnm']))		{			$msg.="<li>Name must be a string";		}				if($msg!="")		{			header("location:register.php?error=".$msg);		}		else 		{						$fnm=$_POST['fnm'];			$unm=$_POST['unm'];			$pwd=$_POST['pwd'];			$gender=$_POST['gender'];			$email=$_POST['mail'];			$contact=$_POST['contact'];					$link=mysql_connect("localhost","root","vertrigo")or die("Can't Connect...");						mysql_select_db("ebook-store",$link) or die("Can't Connect to Database");									$result = mysql_query("SELECT * FROM  customerdetail WHERE username = '$unm'");			$num_rows = mysql_num_rows($result);			if($num_rows!="")			{			$message = "Username already exists";			echo "<script type='text/javascript'>alert('$message');</script>";			header("location:register.php?error=".$message);			}						else			{			$query="insert into customerdetail(id,firstname,username,password,gender,email,contact)			values(NULL,'$fnm','$unm','$pwd','$gender','$email','$contact')";						mysql_query($query,$link) or die("Can't Execute Query");			header("location:register.php?ok=1");			}					}	}	else	{		header("location:index.php");	}?>